  /**
   * Method to modify the attribute request to set IncludeList and ExcludeList for role and source.
   * This method converts Set operation into Add and Remove operation. This would avoid overwriting
   * any data present on the end system but not yet synced to IdentityIQ when this request was triggered.
   * For example :
   * Consider that IdentityIQ has "A,B,C" as IncludeList for role R, User triggers a request from 
   * IdentityIQ to remove A and add D. In this case, IdentityIQ sends a plan to connector to SET B,C,D 
   * as the values for IncludeList for role R. If ACF2 system has A,B,C,E as IncludeList for role R, this
   * request is going to overwrite it to set B,C,D, hence effectively unintentionally removing E from the 
   * list. This method changes SET "B,C,D" to ADD "D" and REMOVE "A", hence avoiding any overwrite.
   * If this is not desired change in behaviour, remove this method and all its references from this 
   * Plan Initializer rule.
   */
  private void modifyIncludeExcludeRequest (Application app, AttributeRequest listAttr, ObjectRequest req) {
      String groupType = req.getType();
      String groupId = req.getNativeIdentity();

      if (listAttr != null) {
          String attrName = listAttr.getName();
          String op = listAttr.getOperation().toString();

          if(op != null && op.equalsIgnoreCase("Set")) {

              listAttr.setOperation(ProvisioningPlan.Operation.Add);

              // code to populate includeList/excludeList present in IdentityIQ database
              ManagedAttribute ma = ManagedAttributer.getEntitlementOrObject(context, app, null, groupId, groupType);
              Object objDBValues = ma.getAttribute(attrName);

              if(objDBValues != null) {
                  List dbList = new ArrayList();
                  if(objDBValues instanceof List) {
                      dbList.addAll(objDBValues);
                  } else if(objDBValues instanceof String) {
                      dbList.add(new String (objDBValues));
                  }

                  if((dbList != null) && !(dbList.isEmpty())) {

                      Object objReqValues =  listAttr.getValue();
                      List reqList = new ArrayList();

                      if(objReqValues instanceof String) {
                          reqList.add(new String (objReqValues));
                      } else if(objReqValues instanceof List) {
                          reqList.addAll(objReqValues);
                      }

                      if(!reqList.isEmpty()) {
                          List tempListDB = new ArrayList();
                          tempListDB.addAll(dbList);
                          dbList.removeAll(reqList);
                          reqList.removeAll(tempListDB);

                          if(!reqList.isEmpty()) {
                              // list values to be added to the role or source includeList or excludeList
                              listAttr.setValue(reqList);
                          } else {
                            req.remove(listAttr);
                          }
                      } else {
                          req.remove(listAttr);
                      }

                      if(!dbList.isEmpty()) {
                          // list values to be removed from the role or source includeList or excludeList
                          // add new attribute request to remove the values
                          AttributeRequest att = new AttributeRequest(attrName, dbList);
                          att.setOperation(ProvisioningPlan.Operation.Remove);
                          req.add(att);
                      }
                  }
              }
          }
      }
  }

  List accounts = plan.getAccountRequests();
  if (accounts != null) {
  for (AccountRequest req : accounts) {
    AccountRequest.Operation op = req.getOperation();
    if (op == AccountRequest.Operation.Create) {
      String userId = req.getNativeIdentity();
      if (userId != null) {
        req.setNativeIdentity(userId.toUpperCase());
      }
    }
    if (op == AccountRequest.Operation.Enable) {
      AttributeRequest att = new AttributeRequest("REVOKE_TYPE", "SUSPEND");
      req.add(att);
    }
    if (op == AccountRequest.Operation.Disable) {
      AttributeRequest att = new AttributeRequest("REVOKE_TYPE", "SUSPEND");
      req.add(att);
    }
    if(context != null)
    {
      IdentityService identityService = new IdentityService(context);
      application = context.getObject(Application.class, req.getApplicationName());
      if(application != null)
      {
        String shouldUseMSAdminDefined = null;
        if(application.getAttributes().get("useMSAdminDefined") != null)
        {
          if(application.getAttributeValue("useMSAdminDefined") instanceof String)
          {
             shouldUseMSAdminDefined = (String) application.getAttributeValue("useMSAdminDefined");
          } 
          else 
           throw new InvalidConfigurationException("Invalid value for attribute useMSAdminDefined in connector configuration",
           "Ensure that the value configured for attribute useMSAdminDefined in connector configuration is of type String.",null);
        }
        String msAdmin=null;
        if(Util.isNotNullOrEmpty(shouldUseMSAdminDefined)) 
        {
          if(shouldUseMSAdminDefined.equals("Y"))
          {
            List idnList = plan.getRequesters();
            if(idnList!=null)
            {
              if(!idnList.isEmpty())
              {
                Identity idn=(Identity) idnList.get(0);
                List lnkList = identityService.getLinks(idn, application);
                if(lnkList!=null)
                {
                  if(!lnkList.isEmpty())
                  {
                    Link lnk = (Link) lnkList.get(0);
                    //log.debug("Adding msAdmin in provisioning plan.");
                    req.addArgument("msAdmin", lnk.getDisplayableName());
                  }
                }
                else
                  log.debug("Requester does not have a link from application " + application.getName());
              }
            }
            else
              log.debug("No requester found.");
          }
        }
        //else
          //log.debug("shouldUseMSAdminDefined is not set to Y");
      }
    }
  }
}
List groups = plan.getObjectRequests();
if (groups != null) {
      for (ObjectRequest req : groups) {
          ObjectOperation op = req.getOp();
          String groupType = req.getType();
          if (op == ObjectOperation.Create){
              String groupId = req.getNativeIdentity();
              if (groupId != null){
                  req.setNativeIdentity(groupId.toUpperCase());
              }
          }

          if((op == ObjectOperation.Modify) && (groupType.equalsIgnoreCase("role") || groupType.equalsIgnoreCase("source"))) {

              Application app = context.getObject(Application.class,req.getApplication());

              AttributeRequest incListAttr = req.getAttributeRequest("IncludeList");
              modifyIncludeExcludeRequest(app, incListAttr, req);

              AttributeRequest excListAttr = req.getAttributeRequest("ExcludeList");
              modifyIncludeExcludeRequest(app, excListAttr, req);
          }

          if(context != null) {
              application = context.getObject(Application.class, req.getApplicationName());
          }
          List attrRequests = req.getAttributeRequests();
          if(attrRequests != null) {
              if(application != null) {
                  Schema grpSchema = application.getSchema("group");
                  if(grpSchema != null) {
                      if(grpSchema.getAttributeNames() != null) {
                          for (AttributeRequest attrreq : attrRequests) {
                              if(grpSchema.getAttributeNames().contains(attrreq.getName())) {
                                  if(attrreq.getValue() instanceof java.lang.String) {
                                      attrreq.setValue(attrreq.getValue().toUpperCase());
                                  }
                              }
                          }
                      }
                  }
              }
          }
          if(context != null)
          {
            IdentityService identityService = new IdentityService(context);
            if(application != null)
            {
              String shouldUseMSAdminDefined = null;
              if(application.getAttributes().get("useMSAdminDefined") != null)
              {
                if(application.getAttributeValue("useMSAdminDefined") instanceof String)
                {
                  shouldUseMSAdminDefined = (String) application.getAttributeValue("useMSAdminDefined");
                } 
                else 
                  throw new InvalidConfigurationException("Invalid value for attribute useMSAdminDefined in connector configuration",
                  "Ensure that the value configured for attribute useMSAdminDefined in connector configuration is of type String.",null);
              }
              String msAdmin=null;
              if(Util.isNotNullOrEmpty(shouldUseMSAdminDefined)) 
              {
                if(shouldUseMSAdminDefined.equals("Y"))
                {
                  List idnList = plan.getRequesters();
                  if(idnList!=null)
                  {
                    if(!idnList.isEmpty())
                    {
                      Identity idn=(Identity) idnList.get(0);
                      List lnkList = identityService.getLinks(idn, application);
                      if(lnkList!=null)
                      {
                        if(!lnkList.isEmpty())
                        {
                          Link lnk = (Link) lnkList.get(0);
                          //log.debug("Adding msAdmin in provisioning plan.");
                          req.addArgument("msAdmin", lnk.getDisplayableName());
                        }
                      }
                      else
                        log.debug("Requester does not have a link from application " + application.getName());
                    }
                  }
                  else
                    log.debug("No requester found.");
                }
              }
              //else
