Rule.Source:
import java.text.SimpleDateFormat;
import java.util.Calendar;
import java.util.Date;

boolean result = false;

try {
  String newHireDate =  newIdentity.getAttribute("hireDate");
  String nsStatus =  newIdentity.getAttribute("nsStatus");
  Link hrLink=getPrimaryAccountLink(newIdentity.getName());
  System.out.println("enter in joiner rule ;nsStatus;"+nsStatus);
  if (newHireDate != null && nsStatus != null && hrLink!=null ) {
    String hrWorkerStatus=hrLink.getAttribute("WORKER_STATUS");
    System.out.println("hrWorkerStatus;;;;;"+hrWorkerStatus);
    if (hrWorkerStatus!=null && hrWorkerStatus.equalsIgnoreCase("Active")) {

      SimpleDateFormat f = new SimpleDateFormat("MM-dd-yyyy");
      Calendar calendar = Calendar.getInstance();
      calendar.set(Calendar.HOUR_OF_DAY, 0);
      calendar.set(Calendar.MINUTE, 0);
      calendar.set(Calendar.SECOND, 0);
      calendar.set(Calendar.MILLISECOND, 0);
      Date today = calendar.getTime();
      System.out.println("Current Date is::::::"+f.format(today));
      Date hireDate = f.parse(newHireDate);

      if (hireDate.equals(today) && nsStatus.equalsIgnoreCase("Joiner Active")) {
        String newStatus = "Joiner Started - " + f.format(today);
        newIdentity.setAttribute("nsStatus", newStatus);
        context.saveObject(newIdentity);
        context.commitTransaction();

        System.out.println("Joiner is able to trigger");
        result = true;
      }
    }
  }
} catch (Exception e) {
  log.error("Error in Joiner Trigger Rule: " + e);
}

return result;
