Rule.Source:
  import java.text.SimpleDateFormat;
   import java.util.Calendar;
   import java.util.Date;
   import sailpoint.tools.Util;
   import sailpoint.object.Link;
  boolean result = false;
  int JoinerDayDiffMax=0;
  int JoinerDayDiffMin=0;
  Logger logs = Logger.getLogger("com.ns.rule.NRRC_Rule_Identity_Trigger_Joiner");
   System.out.println("Joiner_Trigger_rule started");
  try {

    String newHireDate =  newIdentity.getAttribute("hireDate");
	String status =  newIdentity.getAttribute("status");
    System.out.println("newHireDate:::::"+newHireDate);
	System.out.println("status:::::"+status);
	Link hrLink=getPrimaryAccountLink(newIdentity.getName());
	Custom cs = context.getObjectByName(Custom.class, "MapOULink");
		if (cs != null) {
			JoinerDayDiffMax =Integer.parseInt(cs.get("JoinerDayDiffMax"));
			JoinerDayDiffMin =Integer.parseInt(cs.get("JoinerDayDiffMin"));
			logs.debug("JoinerDayDiffMax : " + JoinerDayDiffMax);
			logs.debug("JoinerDayDiffMin : " + JoinerDayDiffMin);
		}
		if(hrLink!=null && hrLink.getAttribute("StatusEmployee")!= null && hrLink.getAttribute("StatusEmployee").equalsIgnoreCase("1")){
    if (newHireDate != null && Util.isNotNullOrEmpty(status)) {

        SimpleDateFormat f = new SimpleDateFormat("MM/dd/yyyy hh:mm:ss a z");
        Calendar calendar = Calendar.getInstance();
        calendar.set(Calendar.HOUR_OF_DAY, 0);
        calendar.set(Calendar.MINUTE, 0);
        calendar.set(Calendar.SECOND, 0);
        calendar.set(Calendar.MILLISECOND, 0);
        Date today = calendar.getTime();
        System.out.println(today+"Current Date is::::::"+f.format(today));
        Date hireDate = f.parse(newHireDate);
		   long diffInMillis = hireDate.getTime() - today.getTime();
		    long diffInDays = diffInMillis / (1000 * 60 * 60 * 24);
        logs.debug("diffInDays : " + diffInDays);
        if (diffInDays <= JoinerDayDiffMax && diffInDays >= JoinerDayDiffMin && status.equalsIgnoreCase("Joiner_Active")) {
          String newStatus = "Joiner-Started";
          newIdentity.setAttribute("status", newStatus);
          context.saveObject(newIdentity);
          context.commitTransaction();
          System.out.println("Joiner is able to trigger");
          result = true;
        }
      }
	 }
    }catch (Exception e) {
	System.out.println("Exception occured in Joiner_Trigger_rule"+e);
    }
  return result;
