Rule.Source:
  import java.text.SimpleDateFormat;
   import java.util.Calendar;
   import java.util.Date;
   import sailpoint.tools.Util;
   import sailpoint.object.Link;
  boolean result = false;
  Logger logs = Logger.getLogger("com.ns.rule.NRRC_Rule_Identity_Trigger_Mover");
   logs.error("Mover_Trigger_rule started");
  try {
    boolean inactive   =  previousIdentity.getAttribute("inactive");
    String prevJobtitle   =  previousIdentity.getAttribute("title");
	String prevDepartment =  previousIdentity.getAttribute("department");
	String prevManager    =  previousIdentity.getManager()!=null?newIdentity.getManager().getName():null;
	String newJobtitle   =  newIdentity.getAttribute("title");
	String newDepartment =  newIdentity.getAttribute("department");
	String newManager    =  newIdentity.getManager()!=null?newIdentity.getManager().getName():null;
    logs.error("prevJobtitle:::"+prevJobtitle+"::::::newJobtitle:::"+newJobtitle);
	logs.error("prevDepartment:::"+prevDepartment+"::::::newDepartment:::"+newDepartment);
	logs.error("prevManager:::"+prevManager+"::::::newManager:::"+newManager);

     if (!inactive && Util.isNotNullOrEmpty(prevJobtitle) && Util.isNotNullOrEmpty(newJobtitle) && Util.isNotNullOrEmpty(prevManager)
	  && Util.isNotNullOrEmpty(newManager) && Util.isNotNullOrEmpty(prevDepartment) && Util.isNotNullOrEmpty(newDepartment)) {
        if(!prevJobtitle.equalsIgnoreCase(newJobtitle) || !prevDepartment.equalsIgnoreCase(newDepartment) || !prevManager.equalsIgnoreCase(newManager)) {
          newIdentity.setAttribute("nsStatus", "Mover-Started");
          context.saveObject(newIdentity);
          context.commitTransaction();
          logs.error("Mover is able to trigger2");
          result = true;
        }
      }
    }catch (Exception e) {
	logs.error("Exception occured in Mover_Trigger_rule"+e);
    }
  return result;
