	Logger logs = Logger.getLogger("com.ns.rule.NRRC_Rule_IdentityAttribute_SetInactive");
	logs.debug("Entering into NRRC_Rule_IdentityAttribute_SetInactive");

  Link link = getPrimaryAccountLink(identity.getName());
  if(link != null &&link.getAttribute("StatusEmployee")!=null && link.getAttribute("StatusEmployee").equalsIgnoreCase("0")) {
     String status = identity.getAttribute("status");
	 logs.debug("NRRC_Rule_IdentityAttribute_SetInactive :::::status::::"+status);
	   if(status != null && !(status.equalsIgnoreCase("Leaver_Active") || status.equalsIgnoreCase("Leaver-Started") || status.equalsIgnoreCase("Leaver-Completed"))){     
		 identity.setAttribute("status","Leaver_Active");
		  context.saveObject(identity);
		  context.commitTransaction();
		  }
		  return true;
	 }
   return false;
