import sailpoint.object.Identity;
  import sailpoint.tools.Util;
  import java.text.SimpleDateFormat;
  import java.util.Calendar;
  import java.util.Date;
  import org.apache.log4j.Logger;

	Logger logs = Logger.getLogger("com.ns.rule.NS_Rule_Hr_WorkDay_CreationRule");
	logs.debug("Entering into NS_Rule_Hr_WorkDay_CreationRule");
    try{
    String name = generateRandomString();
    identity.setName(name);
    SimpleDateFormat f = new SimpleDateFormat("MM-dd-yyyy");
    Calendar calendar = Calendar.getInstance();
    calendar.set(Calendar.HOUR_OF_DAY, 0);
    calendar.set(Calendar.MINUTE, 0);
    calendar.set(Calendar.SECOND, 0);
    calendar.set(Calendar.MILLISECOND, 0);
    Date datep = calendar.getTime();
    logs.debug("Current Date is:"+f.format(datep));
    String hireDate = account.getStringAttribute("HIREDATE");
    int daysDifference = 0;
    if(hireDate!=null){
      Date hireDateFormat = f.parse(hireDate);
      logs.debug("Hire Date is:"+hireDate);
      String employeeType = account.getStringAttribute("EMPLOYEE_TYPE");
      if(Util.isNotNullOrEmpty(employeeType)&&employeeType.equalsIgnoreCase("Partner")){
        if(hireDateFormat.compareTo(datep) == 0){
          identity.setAttribute("nsStatus","Partner_Joiner_Active");
        }else{
          identity.setAttribute("nsStatus","Partner_Joiner_Pending");
        }
      }else{
          //For Now changing to Joiner-COmpleted
        if(hireDateFormat.compareTo(datep) == 0){
          identity.setAttribute("nsStatus","Joiner_Active");
        }else{
          identity.setAttribute("nsStatus","Joiner_Pending");
        }
      }
    }
  }catch(Exception e){
    logs.error("Exception in processing Hire Date logic", e);
  }
