Rule.Source:
 import java.text.SimpleDateFormat;
	import java.util.Calendar;
	import java.util.Date;
	import sailpoint.object.Identity;
	import sailpoint.tools.Util;
	import org.apache.log4j.Logger;

	Logger logs = Logger.getLogger("com.ns.workflow.NS_Rule_Identity_Trigger_Joiner");
	logs.debug("Entering NS_Rule_Identity_Trigger_Joiner ");

	boolean result = false;

	try {
	String newHireDate =  newIdentity.getAttribute("hireDate");
	String nsStatus =  newIdentity.getAttribute("nsStatus");
	String terminationDate =  newIdentity.getAttribute("terminationDate");
	logs.debug("enter in joiner rule ;nsStatus;"+nsStatus);
		if (newHireDate != null & nsStatus != null  ) {
		  if (Util.isNotNullOrEmpty(nsStatus) && Util.isNotNullOrEmpty(newHireDate)) {
			SimpleDateFormat f = new SimpleDateFormat("MM-dd-yyyy");
			Calendar calendar = Calendar.getInstance();
			calendar.set(Calendar.HOUR_OF_DAY, 0);
			calendar.set(Calendar.MINUTE, 0);
			calendar.set(Calendar.SECOND, 0);
			calendar.set(Calendar.MILLISECOND, 0);
			Date today = calendar.getTime();
			logs.debug("Current Date is::::::"+f.format(today));
			Date hireDate = f.parse(newHireDate);
			Date endDate = f.parse(terminationDate);
			long hireDiffInMillis = hireDate.getTime() - today.getTime();
			long hireDiffInDays = hireDiffInMillis / (1000 * 60 * 60 * 24);

			long termdiffInMillis = endDate.getTime() - today.getTime();
			long termdiffInDays = termdiffInMillis / (1000 * 60 * 60 * 24);
			logs.debug("Days difference between today and hire date: " + hireDiffInDays);
			if (hireDiffInDays >0 && hireDiffInDays <=22 && nsStatus.equalsIgnoreCase("Joiner_Active")) {
			  String newStatus = "Joiner-Started " + f.format(today);
			  newIdentity.setAttribute("nsStatus", newStatus);
			  context.saveObject(newIdentity);
			  context.commitTransaction();
			  logs.debug("Joiner is able to trigger");
			  result = true;
			}else if(hireDiffInDays >0 && hireDiffInDays <=22 && nsStatus.equalsIgnoreCase("Leaver-Final-Phase-Completed") && termdiffInDays >0) {
			  String newStatus = "Rehire-Started " + f.format(today);
			  newIdentity.setAttribute("nsStatus", newStatus);
			  context.saveObject(newIdentity);
			  context.commitTransaction();
			  result = true;
			}
		  }
		}
	  } catch (Exception e) {
		logs.error("Error in Joiner Trigger Rule: " + e);
	  }

	return result;
