Rule.Source:
    import java.text.SimpleDateFormat;
	import java.util.Calendar;
	import java.util.Date;
	import sailpoint.object.Identity;
	import sailpoint.tools.Util;
	import org.apache.log4j.Logger;
	import sailpoint.object.Custom;

	Logger logs = Logger.getLogger("com.ns.rule.NS_Rule_Identity_Trigger_Leaver_Final_Phase");
	logs.debug("Entering into NS_Rule_Identity_Trigger_Leaver_Final_Phase:::"+newIdentity.getName());

	boolean result = false;
    int leaverDayDiffMax=0;
	int leaverDayDiffMin=0;
	try {
	String terminationDate =  newIdentity.getAttribute("terminationDate");
	String nsStatus =  newIdentity.getAttribute("nsStatus");
	logs.debug("enter in leaver rule:::nsStatus::::::"+nsStatus);
	Custom cs = context.getObjectByName(Custom.class, "NS_CustomObject_IdentityEvent_Leaver");
		if (cs != null) {
			leaverDayDiffMax =Integer.parseInt(cs.get("LeaverFinalMaxDay"));
			leaverDayDiffMin =Integer.parseInt(cs.get("LeaverFinalMinDay"));
			logs.debug("LeaverDayDiffMax : " + leaverDayDiffMax);
			logs.debug("LeaverDayDiffMin : " + leaverDayDiffMin);
		}
		logs.debug("terminationDate : " + terminationDate);
		  if (Util.isNotNullOrEmpty(nsStatus) && Util.isNotNullOrEmpty(terminationDate)) {
			SimpleDateFormat f = new SimpleDateFormat("MM-dd-yyyy");
			Calendar calendar = Calendar.getInstance();
			calendar.set(Calendar.HOUR_OF_DAY, 0);
			calendar.set(Calendar.MINUTE, 0);
			calendar.set(Calendar.SECOND, 0);
			calendar.set(Calendar.MILLISECOND, 0);
			Date today = calendar.getTime();
			Date terminationDateObj = f.parse(terminationDate);
			long diffInMillis = terminationDateObj.getTime()- today.getTime();
			long diffInDays = diffInMillis / (1000 * 60 * 60 * 24);

			logs.debug("Days difference between today and termination date: " + diffInDays);
			if (diffInDays > leaverDayDiffMin && diffInDays <= leaverDayDiffMax && nsStatus.equalsIgnoreCase("Leaver-Ist-Phase-Completed")) {
			  String newStatus = "Leaver-Final-Phase-Started";
			  newIdentity.setAttribute("nsStatus", newStatus);
			  context.saveObject(newIdentity);
			  context.commitTransaction();
			  logs.debug("Leaver is able to trigger");
			  result = true;
			}
		  }
	  } catch (Exception e) {
		logs.error("Error in Leaver Trigger Rule: " + e);
	  }

	return result;
