	    import sailpoint.object.ProvisioningPlan;
        import sailpoint.object.Identity;
        import sailpoint.object.Custom;
		import sailpoint.object.Attributes;
        import sailpoint.object.ProvisioningPlan.AccountRequest;
        import org.apache.log4j.Logger;

        Logger logs = Logger.getLogger("com.ns.workflow.NRRC_Workflow_Leaver");

        logs.debug("Entering into the step Leaver Disable plan update");
        List applicationToExclude=null;
		Set accReqToBeRemoved = new HashSet();
        Custom customObject = context.getObjectByName(Custom.class,"NRRC_CustomObject_IdentityEvent_Leaver");
        if(customObject !=null){
        Attributes attributes = customObject.getAttributes();
        Map customObjectMap = attributes.getMap();
			if(customObjectMap != null){
			  applicationToExclude = customObjectMap.get("applicationToExclude");
			}
        }
		logs.debug("applicationToExclude :: "+applicationToExclude);
        List accountRequests = plan.getAccountRequests();
		if(accountRequests != null && accountRequests.size()>0){
			for (AccountRequest accountRequest: accountRequests){
				if((applicationToExclude.contains(accountRequest.getApplicationName()))){
					accReqToBeRemoved.add(accountRequest);
				}
			}
		}

		for(AccountRequest req : accReqToBeRemoved){
			plan.remove(req);
		}
		wfcontext.getWorkflowCase().put("wfCurrentStatus", "Update Plan");
		logs.debug("plan:::::::::"+plan.toXml());
        return plan;

