        logs.debug("Entering into the step Leaver Disable plan update");
        List applicationToInclude=null;
		Set accReqToBeRemoved = new HashSet();
        Custom customObject = context.getObjectByName(Custom.class,"NS_CustomObject_IdentityEvent_Leaver");
        if(customObject !=null){
        Attributes attributes = customObject.getAttributes();
        Map customObjectMap = attributes.getMap();
			if(customObjectMap != null){
			  applicationToInclude = customObjectMap.get("applicationToInclude");
			}
        }
		logs.debug("applicationToInclude :: "+applicationToInclude);
        List accountRequests = plan.getAccountRequests();
		if(accountRequests != null && accountRequests.size()>0){
			for (AccountRequest accountRequest: accountRequests){
				if(!(applicationToInclude.contains(accountRequest.getApplicationName()))){
					accReqToBeRemoved.add(accountRequest);
				}
				if(isDisabledApp(accountRequest.getApplicationName(),identityName)){
					accReqToBeRemoved.add(accountRequest);
				}
			}
		}

		for(AccountRequest req : accReqToBeRemoved){
			plan.remove(req);
		}
		logs.debug("plan:::::::::"+plan.toXml());
        return plan;

